import React from 'react';
import { useQuery } from '@apollo/client';
import { GET_TRANSACTIONS } from '../queries'; // Import de la requête GraphQL
import '../styles/styles.css'; // Import des styles globaux

function TransactionList() {
  const { loading, error, data } = useQuery(GET_TRANSACTIONS);

  if (loading) return <p>Chargement des transactions...</p>;
  if (error) return <p>Erreur : {error.message}</p>;

  return (
      <div className="container">
        <h2>Liste des Transactions</h2>
        <div className="transaction-container">
          {data.allTransactions.length === 0 ? (
              <p>Aucune transaction disponible</p>
          ) : (
              data.allTransactions.map((transaction) => (
                  <div key={transaction.id} className="card">
                    <h3>Transaction #{transaction.id}</h3>
                    <p><strong>Montant :</strong> {transaction.montant} €</p>
                    <p><strong>Type :</strong> {transaction.type}</p>
                    <p><strong>Date :</strong> {new Date(transaction.date).toLocaleDateString()}</p>
                    <p><strong>Compte associé :</strong> ID {transaction.compte.id}</p>
                  </div>
              ))
          )}
        </div>
      </div>
  );
}

export default TransactionList;
